import React from "react";
import { motion } from "framer-motion";
import {
  FiCalendar,
  FiMapPin,
  FiMail,
  FiPhone,
  FiDownload,
  FiAward,
} from "react-icons/fi";
import { usePortfolio } from "../App";

const About = () => {
  const { data } = usePortfolio();
  const { personal, about, experience, education, skills } = data;

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
      },
    },
  };

  return (
    <div className="min-h-screen pt-20">
      {/* Hero Section */}
      <motion.section
        className="section-padding"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <div className="container-custom">
          <div className="grid lg:grid-cols-2 gap-12 items-center">
            <motion.div variants={itemVariants}>
              <h1 className="text-5xl font-bold mb-6">
                About <span className="gradient-text">Me</span>
              </h1>
              <p className="text-xl text-gray-300 mb-8">{about.summary}</p>

              <div className="space-y-4 mb-8">
                {about.details.map((detail, index) => (
                  <motion.div
                    key={index}
                    className="flex items-center text-gray-400"
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                  >
                    <span className="text-lg mr-3">{detail}</span>
                  </motion.div>
                ))}
              </div>

              <div className="grid grid-cols-2 gap-4 mb-8">
                <div className="flex items-center text-gray-400">
                  <FiMail className="mr-3 text-primary-500" />
                  <span>{personal.email}</span>
                </div>
                <div className="flex items-center text-gray-400">
                  <FiPhone className="mr-3 text-primary-500" />
                  <span>{personal.phone}</span>
                </div>
                <div className="flex items-center text-gray-400">
                  <FiMapPin className="mr-3 text-primary-500" />
                  <span>{personal.location}</span>
                </div>
                <div className="flex items-center text-gray-400">
                  <FiDownload className="mr-3 text-primary-500" />
                  <a
                    href={personal.resume}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="hover:text-white transition-colors"
                  >
                    Download CV
                  </a>
                </div>
              </div>
            </motion.div>

            <motion.div className="relative" variants={itemVariants}>
              <div className="relative z-10">
                <img
                  src={personal.profileImage}
                  alt={personal.name}
                  className="w-full max-w-lg mx-auto rounded-2xl shadow-2xl"
                />
                <div className="absolute -inset-4 bg-gradient-to-r from-primary-500 to-primary-600 rounded-2xl blur-lg opacity-30 animate-pulse-slow" />
              </div>
            </motion.div>
          </div>
        </div>
      </motion.section>

      {/* Experience Section */}
      <motion.section
        className="section-padding bg-black/20"
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
      >
        <div className="container-custom">
          <motion.h2
            className="text-4xl font-bold text-center mb-16"
            variants={itemVariants}
          >
            Work <span className="gradient-text">Experience</span>
          </motion.h2>

          <div className="max-w-4xl mx-auto">
            {experience.map((exp, index) => (
              <motion.div
                key={exp.id}
                className="relative mb-12 last:mb-0"
                variants={itemVariants}
                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.2 }}
                viewport={{ once: true }}
              >
                {/* Timeline line */}
                {index !== experience.length - 1 && (
                  <div className="absolute left-6 top-16 w-0.5 h-24 bg-gradient-to-b from-primary-500 to-transparent" />
                )}

                <div className="flex items-start">
                  <div className="w-12 h-12 bg-primary-500 rounded-full flex items-center justify-center mr-6 flex-shrink-0">
                    <FiAward className="text-white" size={20} />
                  </div>

                  <div className="glass-effect p-6 rounded-xl flex-1">
                    <div className="flex flex-wrap items-center justify-between mb-4">
                      <h3 className="text-xl font-semibold">{exp.position}</h3>
                      <span className="flex items-center text-primary-400 text-sm">
                        <FiCalendar className="mr-2" size={14} />
                        {exp.period}
                      </span>
                    </div>

                    <h4 className="text-primary-400 font-medium mb-3">
                      {exp.company}
                    </h4>
                    <p className="text-gray-400 mb-4">{exp.description}</p>

                    <div className="flex flex-wrap gap-2">
                      {exp.technologies.map((tech) => (
                        <span
                          key={tech}
                          className="px-3 py-1 bg-primary-500/20 text-primary-400 rounded-full text-sm"
                        >
                          {tech}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </motion.section>

      {/* Education Section */}
      <motion.section
        className="section-padding"
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
      >
        <div className="container-custom">
          <motion.h2
            className="text-4xl font-bold text-center mb-16"
            variants={itemVariants}
          >
            <span className="gradient-text">Education</span>
          </motion.h2>

          <div className="max-w-4xl mx-auto grid md:grid-cols-2 gap-8">
            {education.map((edu, index) => (
              <motion.div
                key={edu.id}
                className="glass-effect p-6 rounded-xl hover-lift"
                variants={itemVariants}
                whileHover={{ scale: 1.02 }}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.2 }}
                viewport={{ once: true }}
              >
                <div className="flex items-center mb-4">
                  <div className="w-10 h-10 bg-primary-500 rounded-lg flex items-center justify-center mr-4">
                    <FiAward className="text-white" size={20} />
                  </div>
                  <div>
                    <h3 className="font-semibold">{edu.degree}</h3>
                    <p className="text-primary-400 text-sm">
                      {edu.institution}
                    </p>
                  </div>
                </div>

                <div className="flex items-center mb-3">
                  <FiCalendar className="mr-2 text-gray-400" size={14} />
                  <span className="text-gray-400 text-sm">{edu.period}</span>
                </div>

                <p className="text-gray-400">{edu.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </motion.section>

      {/* Skills Section */}
      <motion.section
        className="section-padding bg-black/20"
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
      >
        <div className="container-custom">
          <motion.h2
            className="text-4xl font-bold text-center mb-16"
            variants={itemVariants}
          >
            Skills & <span className="gradient-text">Expertise</span>
          </motion.h2>

          {/* Technical Skills */}
          <div className="mb-16">
            <h3 className="text-2xl font-semibold mb-8 text-center">
              Technical Skills
            </h3>

            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {skills.technical.map((skill, index) => (
                <motion.div
                  key={skill.name}
                  className="glass-effect p-4 rounded-xl"
                  variants={itemVariants}
                  initial={{ opacity: 0, scale: 0.9 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  transition={{ delay: index * 0.05 }}
                  viewport={{ once: true }}
                >
                  <div className="flex justify-between items-center mb-2">
                    <span className="font-medium">{skill.name}</span>
                    <span className="text-sm text-primary-400">
                      {skill.level}%
                    </span>
                  </div>
                  <div className="w-full bg-gray-700 rounded-full h-2">
                    <motion.div
                      className="bg-gradient-to-r from-primary-500 to-primary-600 h-2 rounded-full"
                      initial={{ width: 0 }}
                      whileInView={{ width: `${skill.level}%` }}
                      transition={{ duration: 1, delay: index * 0.05 }}
                      viewport={{ once: true }}
                    />
                  </div>
                  <div className="text-xs text-gray-500 mt-1">
                    {skill.category}
                  </div>
                </motion.div>
              ))}
            </div>
          </div>

          {/* Soft Skills */}
          <div>
            <h3 className="text-2xl font-semibold mb-8 text-center">
              Soft Skills
            </h3>

            <div className="flex flex-wrap justify-center gap-4">
              {skills.soft.map((skill, index) => (
                <motion.span
                  key={skill}
                  className="px-4 py-2 glass-effect rounded-full text-sm font-medium"
                  initial={{ opacity: 0, scale: 0 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  transition={{ delay: index * 0.1 }}
                  whileHover={{ scale: 1.05 }}
                  viewport={{ once: true }}
                >
                  {skill}
                </motion.span>
              ))}
            </div>
          </div>
        </div>
      </motion.section>
    </div>
  );
};

export default About;
