import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useForm } from "react-hook-form";
import toast from "react-hot-toast";
import {
  FiEdit3,
  FiSave,
  FiPlus,
  FiTrash2,
  FiUser,
  FiBriefcase,
  FiBook,
  FiCode,
  FiFolder,
  FiMessageSquare,
  FiSettings,
  FiEye,
  FiEyeOff,
  FiDownload,
  FiUpload,
  FiLogOut,
  FiRotateCcw,
  FiDroplet,
  FiType,
  FiGrid,
  FiZap,
  FiLayers,
  FiCheck,
  FiImage,
  FiGlobe,
  FiMonitor,
  FiX,
  FiInstagram,
  FiLinkedin,
  FiGithub,
  FiTwitter,
  FiFacebook,
  FiYoutube,
} from "react-icons/fi";
import { usePortfolio } from "../App";
import { useAuth } from "../contexts/AuthContext";
import { useTheme } from "../contexts/ThemeContext";
import { useSettings } from "../contexts/SettingsContext";

const Dashboard = () => {
  const { data, updateData } = usePortfolio();
  const { logout } = useAuth();
  const { isDarkMode } = useTheme();
  const [activeTab, setActiveTab] = useState("personal");
  const [editMode, setEditMode] = useState({});
  const [showPreview, setShowPreview] = useState(false);

  const tabs = [
    { id: "personal", name: "Personal Info", icon: FiUser },
    { id: "experience", name: "Experience", icon: FiBriefcase },
    { id: "education", name: "Education", icon: FiBook },
    { id: "skills", name: "Skills", icon: FiCode },
    { id: "projects", name: "Projects", icon: FiFolder },
    { id: "testimonials", name: "Testimonials", icon: FiMessageSquare },
    { id: "blog", name: "Blog", icon: FiEdit3 },
    { id: "social", name: "Social Media", icon: FiGlobe },
    { id: "resume", name: "Resume", icon: FiDownload },
    { id: "settings", name: "Settings", icon: FiSettings },
  ];

  const toggleEditMode = (section, id = null) => {
    const key = id ? `${section}_${id}` : section;
    setEditMode((prev) => ({
      ...prev,
      [key]: !prev[key],
    }));
  };

  const saveData = (section, updatedData, id = null) => {
    const newData = { ...data };

    if (id) {
      // Update specific item in array
      const index = newData[section].findIndex((item) => item.id === id);
      if (index !== -1) {
        newData[section][index] = {
          ...newData[section][index],
          ...updatedData,
        };
      }
    } else {
      // Update entire section
      newData[section] = { ...newData[section], ...updatedData };
    }

    updateData(newData);
    toast.success("Data saved successfully!");
    toggleEditMode(section, id);
  };

  const addNewItem = (section, newItem) => {
    const newData = { ...data };
    const newId = Math.max(...newData[section].map((item) => item.id || 0)) + 1;
    newData[section].push({ ...newItem, id: newId });
    updateData(newData);
    toast.success("Item added successfully!");
  };

  const deleteItem = (section, id) => {
    if (window.confirm("Are you sure you want to delete this item?")) {
      const newData = { ...data };
      newData[section] = newData[section].filter((item) => item.id !== id);
      updateData(newData);
      toast.success("Item deleted successfully!");
    }
  };

  const exportData = () => {
    const dataStr = JSON.stringify(data, null, 2);
    const dataUri =
      "data:application/json;charset=utf-8," + encodeURIComponent(dataStr);
    const exportFileDefaultName = "portfolio-data.json";

    const linkElement = document.createElement("a");
    linkElement.setAttribute("href", dataUri);
    linkElement.setAttribute("download", exportFileDefaultName);
    linkElement.click();

    toast.success("Data exported successfully!");
  };

  const importData = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const importedData = JSON.parse(e.target.result);
          updateData(importedData);
          toast.success("Data imported successfully!");
        } catch (error) {
          toast.error("Invalid JSON file");
        }
      };
      reader.readAsText(file);
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 0.3,
      },
    },
  };

  const handleLogout = () => {
    if (window.confirm("Are you sure you want to logout?")) {
      logout();
      toast.success("Logged out successfully!");
    }
  };

  return (
    <div
      className={`min-h-screen pt-20 transition-colors duration-300 ${
        isDarkMode
          ? "bg-gradient-to-br from-gray-900 via-black to-gray-900"
          : "bg-gradient-to-br from-blue-50 via-white to-indigo-50"
      }`}
    >
      <div className="container-custom py-8">
        {/* Header */}
        <motion.div
          className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <div>
            <h1
              className={`text-4xl font-bold mb-2 ${
                isDarkMode ? "text-white" : "text-gray-900"
              }`}
            >
              Portfolio <span className="gradient-text">Dashboard</span>
            </h1>
            <p className={`${isDarkMode ? "text-gray-400" : "text-gray-600"}`}>
              Manage your portfolio content and settings
            </p>
          </div>

          <div className="flex gap-4 mt-4 md:mt-0">
            <button
              onClick={() => setShowPreview(!showPreview)}
              className={`flex items-center px-4 py-2 rounded-lg transition-colors ${
                isDarkMode
                  ? "bg-gray-700 hover:bg-gray-600 text-white"
                  : "bg-gray-200 hover:bg-gray-300 text-gray-900"
              }`}
            >
              {showPreview ? (
                <FiEyeOff className="mr-2" />
              ) : (
                <FiEye className="mr-2" />
              )}
              {showPreview ? "Hide Preview" : "Show Preview"}
            </button>

            <button
              onClick={exportData}
              className="flex items-center px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition-colors"
            >
              <FiDownload className="mr-2" />
              Export Data
            </button>

            <label className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors cursor-pointer">
              <FiUpload className="mr-2" />
              Import Data
              <input
                type="file"
                accept=".json"
                onChange={importData}
                className="hidden"
              />
            </label>

            <button
              onClick={handleLogout}
              className="flex items-center px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg transition-colors"
            >
              <FiLogOut className="mr-2" />
              Logout
            </button>
          </div>
        </motion.div>

        <div className="grid lg:grid-cols-4 gap-8">
          {/* Sidebar */}
          <motion.div
            className="lg:col-span-1"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <div className="glass-effect rounded-xl p-4 sticky top-24">
              <nav className="space-y-2">
                {tabs.map((tab) => {
                  const Icon = tab.icon;
                  return (
                    <motion.button
                      key={tab.id}
                      onClick={() => setActiveTab(tab.id)}
                      className={`w-full flex items-center px-4 py-3 rounded-lg transition-all ${
                        activeTab === tab.id
                          ? "bg-primary-500 text-white"
                          : "text-gray-300 hover:bg-white/10"
                      }`}
                      variants={itemVariants}
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      <Icon className="mr-3" size={18} />
                      <span className="font-medium">{tab.name}</span>
                    </motion.button>
                  );
                })}
              </nav>
            </div>
          </motion.div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            <AnimatePresence mode="wait">
              <motion.div
                key={activeTab}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
                className="glass-effect rounded-xl p-6"
              >
                {/* Personal Info Tab */}
                {activeTab === "personal" && (
                  <PersonalInfoSection
                    data={data.personal}
                    editMode={editMode.personal}
                    onToggleEdit={() => toggleEditMode("personal")}
                    onSave={(updatedData) => saveData("personal", updatedData)}
                  />
                )}

                {/* Experience Tab */}
                {activeTab === "experience" && (
                  <ExperienceSection
                    data={data.experience}
                    editMode={editMode}
                    onToggleEdit={toggleEditMode}
                    onSave={saveData}
                    onAdd={addNewItem}
                    onDelete={deleteItem}
                  />
                )}

                {/* Education Tab */}
                {activeTab === "education" && (
                  <EducationSection
                    data={data.education}
                    editMode={editMode}
                    onToggleEdit={toggleEditMode}
                    onSave={saveData}
                    onAdd={addNewItem}
                    onDelete={deleteItem}
                  />
                )}

                {/* Skills Tab */}
                {activeTab === "skills" && (
                  <SkillsSection
                    data={data.skills}
                    editMode={editMode.skills}
                    onToggleEdit={() => toggleEditMode("skills")}
                    onSave={(updatedData) => saveData("skills", updatedData)}
                  />
                )}

                {/* Projects Tab */}
                {activeTab === "projects" && (
                  <ProjectsSection
                    data={data.projects}
                    editMode={editMode}
                    onToggleEdit={toggleEditMode}
                    onSave={saveData}
                    onAdd={addNewItem}
                    onDelete={deleteItem}
                  />
                )}

                {/* Testimonials Tab */}
                {activeTab === "testimonials" && (
                  <TestimonialsSection
                    data={data.testimonials}
                    editMode={editMode}
                    onToggleEdit={toggleEditMode}
                    onSave={saveData}
                    onAdd={addNewItem}
                    onDelete={deleteItem}
                  />
                )}

                {/* Blog Tab */}
                {activeTab === "blog" && (
                  <BlogSection
                    data={data.blog}
                    editMode={editMode}
                    onToggleEdit={toggleEditMode}
                    onSave={saveData}
                    onAdd={addNewItem}
                    onDelete={deleteItem}
                  />
                )}

                {/* Social Media Tab */}
                {activeTab === "social" && (
                  <SocialMediaSection
                    data={data.social}
                    editMode={editMode.social}
                    onToggleEdit={() => toggleEditMode("social")}
                    onSave={(updatedData) => saveData("social", updatedData)}
                  />
                )}

                {/* Resume Tab */}
                {activeTab === "resume" && (
                  <ResumeSection
                    data={data.resume}
                    editMode={editMode.resume}
                    onToggleEdit={() => toggleEditMode("resume")}
                    onSave={(updatedData) => saveData("resume", updatedData)}
                  />
                )}

                {/* Settings Tab */}
                {activeTab === "settings" && <SettingsSection />}
              </motion.div>
            </AnimatePresence>
          </div>
        </div>
      </div>
    </div>
  );
};

// Personal Info Section Component
const PersonalInfoSection = ({ data, editMode, onToggleEdit, onSave }) => {
  const { register, handleSubmit, reset } = useForm({
    defaultValues: data,
  });

  React.useEffect(() => {
    reset(data);
  }, [data, reset]);

  const onSubmit = (formData) => {
    onSave(formData);
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold">Personal Information</h2>
        <button
          onClick={onToggleEdit}
          className="flex items-center px-4 py-2 bg-primary-600 hover:bg-primary-700 rounded-lg transition-colors"
        >
          <FiEdit3 className="mr-2" />
          {editMode ? "Cancel" : "Edit"}
        </button>
      </div>

