                  <div>
                    <label className="block text-sm font-medium mb-2">
                      Message
                    </label>
                    <textarea
                      {...register("message", { required: true })}
                      rows={4}
                      className="w-full px-3 py-2 bg-gray-700 rounded-lg"
                      placeholder="Share your testimonial message here..."
                    />
