import React from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import {
  FiCode,
  FiSmartphone,
  FiSearch,
  FiArrowRight,
  FiCheck,
} from "react-icons/fi";
import { usePortfolio } from "../App";

const Services = () => {
  const { data } = usePortfolio();
  const { services } = data;

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
      },
    },
  };

  const serviceIcons = {
    "üåê": FiCode,
    "üì±": FiSmartphone,
    "üîç": FiSearch,
  };

  return (
    <div className="min-h-screen pt-20">
      {/* Header */}
      <motion.section
        className="section-padding"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <div className="container-custom">
          <div className="text-center mb-16">
            <h1 className="text-5xl font-bold mb-6">
              My <span className="gradient-text">Services</span>
            </h1>
            <p className="text-xl text-gray-400 max-w-3xl mx-auto">
              I offer a range of professional services to help bring your ideas
              to life. From concept to deployment, I'm here to help you succeed.
            </p>
          </div>
        </div>
      </motion.section>

      {/* Services Grid */}
      <motion.section
        className="section-padding bg-black/20"
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
      >
        <div className="container-custom">
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {services.map((service, index) => {
              const IconComponent = serviceIcons[service.icon] || FiCode;

              return (
                <motion.div
                  key={service.id}
                  className="glass-effect p-8 rounded-xl hover-lift group"
                  variants={itemVariants}
                  whileHover={{ scale: 1.02, y: -5 }}
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.2 }}
                  viewport={{ once: true }}
                >
                  <div className="text-center mb-6">
                    <div className="w-16 h-16 bg-gradient-to-r from-primary-500 to-primary-600 rounded-2xl flex items-center justify-center mx-auto mb-4 group-hover:scale-110 transition-transform duration-300">
                      <span className="text-3xl">{service.icon}</span>
                    </div>
                    <h3 className="text-2xl font-bold mb-3">{service.title}</h3>
                    <p className="text-gray-400">{service.description}</p>
                  </div>

                  <div className="space-y-3 mb-8">
                    {service.features.map((feature, featureIndex) => (
                      <motion.div
                        key={featureIndex}
                        className="flex items-center text-gray-300"
                        initial={{ opacity: 0, x: -20 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        transition={{ delay: index * 0.2 + featureIndex * 0.1 }}
                        viewport={{ once: true }}
                      >
                        <FiCheck className="text-primary-500 mr-3" size={16} />
                        <span>{feature}</span>
                      </motion.div>
                    ))}
                  </div>

                  <div className="text-center">
                    <Link
                      to="/contact"
                      className="inline-flex items-center text-primary-400 hover:text-primary-300 transition-colors group-hover:scale-105 transform duration-300"
                    >
                      Get Started <FiArrowRight className="ml-2" />
                    </Link>
                  </div>
                </motion.div>
              );
            })}
          </div>
        </div>
      </motion.section>

      {/* Process Section */}
      <motion.section
        className="section-padding"
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
      >
